import { Injectable } from '@angular/core';
import { TranslateService } from '@ngx-translate/core';
import { map, Observable, of } from 'rxjs';

import { LanguageTranslation } from './language.enum';
import { LocalizationModule } from './localization.module';

const enum LanguagePreferences {
  UserPreference,
  TenantPreference,
  EnvPreference,
  BrowserPreference,
}

@Injectable({
  providedIn: LocalizationModule,
})
export class TranslationService {
  constructor(public translate: TranslateService) {}

  public userPreference: LanguageTranslation | undefined;

  public tenantPreference: LanguageTranslation | undefined;

  public envPreference: LanguageTranslation | undefined;

  public browserPreference: LanguageTranslation | undefined;

  protected prefPriority = [
    LanguagePreferences.UserPreference,
    LanguagePreferences.TenantPreference,
    LanguagePreferences.EnvPreference,
    LanguagePreferences.BrowserPreference,
  ];

  init(langs: LanguageTranslation[] = [LanguageTranslation.ENGLISH]) {
    this.translate.addLangs(langs);

    // this language will be used as a fallback when a translation isn't found in the current language
    this.translate.setDefaultLang(LanguageTranslation.ENGLISH);

    this.browserPreference = navigator.language as LanguageTranslation;
  }

  reset(): Observable<string> {
    let langToSet: LanguageTranslation | undefined;
    for (let i = 0; i < this.prefPriority.length; i++) {
      if (
        this.prefPriority[i] === LanguagePreferences.UserPreference &&
        this.userPreference
      ) {
        langToSet = this.userPreference;
        break;
      } else if (
        this.prefPriority[i] === LanguagePreferences.TenantPreference &&
        this.tenantPreference
      ) {
        langToSet = this.tenantPreference;
        break;
      } else if (
        this.prefPriority[i] === LanguagePreferences.EnvPreference &&
        this.envPreference
      ) {
        langToSet = this.envPreference;
        break;
      } else if (
        this.prefPriority[i] === LanguagePreferences.BrowserPreference &&
        this.browserPreference
      ) {
        langToSet = this.browserPreference;
        break;
      } else {
        // Unmatched preference
        continue;
      }
    }

    if (!langToSet) {
      return of(this.translate.currentLang);
    }
    // get the available languages
    const languages = this.translate.getLangs();
    for (const lang of languages) {
      if (langToSet.includes(lang)) {
        return this.translate
          .use(lang)
          .pipe(map(() => this.translate.currentLang));
      }
    }

    return of(this.translate.currentLang);
  }
}

import { Inject, Injectable } from '@angular/core';
import { InMemoryStorageService, StorageService } from 'ngx-webstorage-service';

import { LoggedInUserDM } from '../auth/models';
import { APPLICATION_STORE, APP_SESSION_STORE } from './keys';
import { StoreKeys } from './store-keys.enum';
import { StoreModule } from './store.module';

@Injectable({
  providedIn: StoreModule,
})
export class UserSessionStoreService {
  constructor(
    @Inject(APPLICATION_STORE) private readonly store: StorageService,
    @Inject(APP_SESSION_STORE) private readonly sessionStore: StorageService,

    private readonly inMemoryStore: InMemoryStorageService
  ) {}

  public saveAccessToken(token: string): boolean {
    this.store.set(StoreKeys.ACCESS_TOKEN_KEY, token);
    return true;
  }

  public getAccessToken(): string {
    return this.store.get(StoreKeys.ACCESS_TOKEN_KEY);
  }

  public removeAccessToken(): boolean {
    this.store.remove(StoreKeys.ACCESS_TOKEN_KEY);
    return true;
  }

  public saveRefreshToken(token: string): boolean {
    this.store.set(StoreKeys.REFRESH_TOKEN_KEY, token);
    return true;
  }

  public getRefreshToken(): string {
    return this.store.get(StoreKeys.REFRESH_TOKEN_KEY);
  }

  public removeRefreshToken(): boolean {
    this.store.remove(StoreKeys.REFRESH_TOKEN_KEY);
    return true;
  }

  public saveTokenExpiry(expires: number): boolean {
    this.store.set(StoreKeys.TOKEN_EXPIRY, expires);
    return true;
  }

  public getTokenExpiry(): number {
    return this.store.get(StoreKeys.TOKEN_EXPIRY);
  }

  public setUser(user: LoggedInUserDM): void {
    this.inMemoryStore.set(StoreKeys.USER_KEY, user);
  }

  public getUser(): LoggedInUserDM {
    return new LoggedInUserDM(this.inMemoryStore.get(StoreKeys.USER_KEY));
  }

  public saveLastAccessedUrl(url: string): void {
    this.sessionStore.set(StoreKeys.LAST_ACCESSED_URL, url);
  }

  public getLastAccessedUrl(): string {
    return this.sessionStore.get(StoreKeys.LAST_ACCESSED_URL);
  }

  public clearAll(): void {
    this.sessionStore.remove(StoreKeys.LAST_ACCESSED_URL);
    this.store.remove(StoreKeys.USER_KEY);
    this.store.remove(StoreKeys.ACCESS_TOKEN_KEY);
    this.store.remove(StoreKeys.REFRESH_TOKEN_KEY);
  }
}

import { Injectable } from '@angular/core';
import { GanttScaleUnits } from '../../enum';
import { GanttScaleService, Timelines } from '../../types';

@Injectable()
export class WeeklyScaleService implements GanttScaleService {
  scale = Timelines.Weekly;
  config() {
    return [
      {
        unit: GanttScaleUnits.Week,
        step: 1,
        format: (date: Date) => this._formatWeeklyScale(date),
      },
      {
        unit: GanttScaleUnits.Day,
        step: 1,
        format: (date: Date) =>
          date.toLocaleString('default', { weekday: 'short' }).charAt(0),
        css: (date: Date) => this._attachWeekendClass(date),
      },
    ];
  }

  private _formatWeeklyScale(date: Date) {
    const noOfDigits = 2;
    return `${date.toLocaleString('default', { month: 'short' })} ${date
      .getDate()
      .toString()
      .padStart(noOfDigits, '0')}, ${date.toLocaleString('default', {
      year: 'numeric',
    })}`;
  }

  private _attachWeekendClass(date: Date) {
    const lastDay = 6;
    if (date.getDay() == 0 || date.getDay() == lastDay) {
      return 'weekend';
    } else {
      return 'weekday';
    }
  }
}

