import {
  HttpEvent,
  HttpHandler,
  HttpInterceptor,
  HttpRequest,
} from '@angular/common/http';
import { Injectable } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Observable, throwError } from 'rxjs';

import { AuthTokenSkipHeader } from '../constants';
import { UserSessionStoreService } from '../store';

@Injectable()
export class AuthInterceptor implements HttpInterceptor {
  constructor(
    private readonly sessionStore: UserSessionStoreService,
    private readonly route: ActivatedRoute,
    private readonly router: Router
  ) {}

  intercept(
    req: HttpRequest<unknown>,
    next: HttpHandler
  ): Observable<HttpEvent<unknown>> {
    if (req.headers.has(AuthTokenSkipHeader) || req.url.includes('i18n/')) {
      const headers = req.headers.delete(AuthTokenSkipHeader);
      return next.handle(req.clone({ headers }));
    }
    const authToken = this.sessionStore.getAccessToken();

    if (authToken) {
      return next.handle(
        req.clone({ setHeaders: { Authorization: `Bearer ${authToken}` } })
      );
    } else {
      if (this.route.snapshot.data['skipAuth']) {
        this.router.navigate(['/']);
      }
      return throwError(() => 'Request forbidden ! No access token available.');
    }
  }
}

import {
  HttpErrorResponse,
  HttpEvent,
  HttpHandler,
  HttpInterceptor,
  HttpRequest,
} from '@angular/common/http';
import { Inject, Injectable } from '@angular/core';
import { get } from 'lodash';
import { catchError, Observable } from 'rxjs';

import { STATUS_CODE } from '../api';
import { ErrToastSkipHeader } from '../constants';
import { IToaster, TOASTER_SERVICE_KEY } from '../toaster';

const errorMsgConst = 'error.error.message.message';

@Injectable()
export class ErrorInterceptor implements HttpInterceptor {
  constructor(
    @Inject(TOASTER_SERVICE_KEY) private readonly toastrService: IToaster
  ) {}

  private _checkTokenExpiryErr(error: HttpErrorResponse): boolean {
    return (
      error.status &&
      error.status === STATUS_CODE.UNAUTHORIZED &&
      error.error &&
      error.error.error &&
      error.error.error.message &&
      error.error.error.message.message === 'TokenExpired'
    );
  }

  intercept(
    req: HttpRequest<unknown>,
    next: HttpHandler
  ): Observable<HttpEvent<unknown>> {
    if (req.headers.has(ErrToastSkipHeader)) {
      const headers = req.headers.delete(ErrToastSkipHeader);
      return next.handle(req.clone({ headers }));
    } else {
      return next.handle(req).pipe(
        catchError((error) => {
          if (
            error instanceof HttpErrorResponse &&
            error.error.error?.statusCode === STATUS_CODE.UNPROCESSABLE_ENTITY
          ) {
            const errMsg = this.getErrMsg(error);
            this.toastrService.error(errMsg, 'ERROR !');
          } else if (
            error instanceof HttpErrorResponse &&
            !this._checkTokenExpiryErr(error)
          ) {
            let errMsg = get(error, errorMsgConst);
            errMsg =
              errMsg ||
              get(
                error,
                'error.error.message',
                'Some error occured. Please try again.'
              );
            this.toastrService.error(errMsg, 'ERROR !');
          } else {
            // Do nothing
          }
          throw error;
        })
      );
    }
  }

  getErrMsg(err: { [key: string | symbol]: any }) {
    const errDetails =
      get(err, 'error.error.details') || get(err, 'error.error.message');
    let errMsg: string | undefined;
    if (Array.isArray(errDetails)) {
      errDetails.forEach((item) => {
        if (errMsg) {
          errMsg = `${errMsg} \
            ${item.path} ${item.message}`;
        } else {
          errMsg = `${item.path} ${item.message}`;
        }
      });
    } else {
      errMsg = errDetails;
    }
    return errMsg;
  }
}

import {
  HttpErrorResponse,
  HttpEvent,
  HttpHandler,
  HttpInterceptor,
  HttpRequest,
} from '@angular/common/http';
import { Injectable } from '@angular/core';
import { catchError, Observable, Subject, switchMap, throwError } from 'rxjs';

import { STATUS_CODE } from '../api';
import { AuthService } from '../auth';
import { UserSessionStoreService } from '../store';

@Injectable()
export class SessionRecoveryInterceptor implements HttpInterceptor {
  constructor(
    private readonly store: UserSessionStoreService,
    private readonly sessionService: AuthService
  ) {}

  private _refreshSubject: Subject<any> = new Subject<any>();

  private _ifTokenExpired() {
    this._refreshSubject.subscribe({
      complete: () => {
        this._refreshSubject = new Subject<any>();
      },
    });
    if (this._refreshSubject.observers.length === 1) {
      this.sessionService.refreshToken().subscribe(this._refreshSubject);
    }
    return this._refreshSubject;
  }

  private _checkTokenExpiryErr(error: HttpErrorResponse): boolean {
    return (
      this._isStatusUnauthorized(error) &&
      error.error &&
      error.error.error &&
      error.error.error.message &&
      (error.error.error.message.message === 'TokenExpired' ||
        error.error.error.message.message === 'TokenRevoked')
    );
  }

  private _isStatusUnauthorized(error: HttpErrorResponse): boolean {
    return !!error.status && error.status === STATUS_CODE.UNAUTHORIZED;
  }

  intercept(
    req: HttpRequest<any>,
    next: HttpHandler
  ): Observable<HttpEvent<any>> {
    if (
      req.url.endsWith('/token-refresh') ||
      req.url.endsWith('/verify-token')
    ) {
      return next.handle(req);
    } else {
      return next.handle(req).pipe(
        catchError((error, caught) => {
          if (error instanceof HttpErrorResponse) {
            if (this._checkTokenExpiryErr(error)) {
              return this._ifTokenExpired().pipe(
                switchMap(() => next.handle(this.updateHeader(req)))
              );
            } else {
              return throwError(() => error);
            }
          }
          return caught;
        })
      );
    }
  }

  updateHeader(req: HttpRequest<any>) {
    const authToken = this.store.getAccessToken();
    if (req.url.endsWith('/logout')) {
      req = req.clone({
        body: { refreshToken: this.store.getRefreshToken() },
        headers: req.headers.set('Authorization', `Bearer ${authToken}`),
      });
    } else {
      req = req.clone({
        headers: req.headers.set('Authorization', `Bearer ${authToken}`),
      });
    }
    return req;
  }
}

