.select-container {
  // width: 20%;
  nb-icon[icon='chevron-down'] {
    cursor: pointer;
  }
  .count-box {
    user-select: none;
    color: var(--text-primary-color);
    cursor: pointer;
  }
  nb-tag {
    position: relative;
    overflow: hidden !important;
    white-space: nowrap;
    padding-right: 1.4rem;
    text-overflow: ellipsis;
    text-align: left;
    display: block;
    text-transform: none;
    background: var(--bb-tag-background);
    border: var(--bb-tag-border);
  }
  nb-icon {
    height: 40px;
    margin-left: -45px;
  }
  .tag-input {
    width: 100%;
    border: 1px solid #ced4da;
    border-radius: 0.25rem;
    padding: 7px;
    font-size: 15px;
    &:focus {
      color: #495057;
      background-color: #fff;
      border-color: #80bdff;
      outline: 0;
      box-shadow: 0 0 0 0.2rem rgba(0, 123, 255, 0.25);
    }
  }
}

.dropdown-panel {
  display: block;
  box-shadow: var(--bb-select-panel-shadow);
  border-radius: var(--border-radius);
  user-select: none;
}

